#include<iostream>#include<vector>#include<iterator>#include<cstdlib>#include<ctime>using namespace std;//有一个很大很大的输入流，大到没有存储器可以将其存储下来， 而且只输入一次，//如何从这个输入流中随机取得n 个记录。采用何种方法,才能在不需要存储数据流的基础上,//获得这 n个等概率的随机数据呢?void SelectElem(int n,vector<int> &output){	srand(time(NULL));	int m=0;	int val=1;	while(cin>>val)	{		if(m<n) 		{			output.push_back(val);			m++;		}		else		{			int current =rand();			if(current%m<n)			output[current%n]=val;   		}				}}int main(){	vector<int> output;	SelectElem(4,output);	copy(output.begin(),output.end(),ostream_iterator<int,char>(cout,"	"));	cout<<endl;}